};

export function DiscussionCard({
  title,
  preview,
  author,
  category,
  replies,
  views,
  timestamp,
}: DiscussionCardProps) {
  const config = roleConfig[author.role];

  return (
    <Card className="hover-elevate overflow-visible border-border/50" data-testid={`card-discussion-${title.toLowerCase().replace(/\s+/g, '-').slice(0, 20)}`}>
