                Create Project
              </Button>
            </CardContent>
          </Card>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {projects.map((project) => (
              <Link key={project.id} href={`/projects/${project.id}`} className="block">
                <Card className="hover-elevate border-border/50 h-full" data-testid={`card-project-${project.id}`}>
                    <CardHeader className="pb-4">
                      <div className="flex items-start justify-between gap-3 mb-2">
                        <h3 className="font-bold text-xl line-clamp-2 flex-1">{project.name}</h3>
                        <Badge 
                          variant={project.status === 'active' ? 'default' : 'secondary'}
                        >
                          {project.status}
                        </Badge>
                      </div>
                    </CardHeader>

                    <CardContent className="pb-4">
                      {project.description && (
                        <p className="text-sm text-muted-foreground line-clamp-3 mb-4 leading-relaxed">
                          {project.description}
                        </p>
                      )}

                      <div className="flex items-center gap-2 text-xs text-muted-foreground">
                        <Calendar className="h-3.5 w-3.5" />
                        <span>Created {format(new Date(project.createdAt!), 'MMM d, yyyy')}</span>
                      </div>
                    </CardContent>

                    <CardFooter className="pt-0">
                      <div className="text-sm font-medium text-primary">View Dashboard â†’</div>
                    </CardFooter>
                  </Card>
              </Link>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

client/src/components/ProfileCard.tsx.content:
import { Card, CardContent, CardFooter } from "@/components/ui/card";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { MapPin, Briefcase } from "lucide-react";

export type UserRole = "coder" | "investor" | "technical";

export interface ProfileCardProps {
  id: string;
  name: string;
  role: UserRole;
  avatar: string;
  tagline: string;
  location: string;
  tags: string[];
  matchScore?: number;
}

const roleConfig = {
  coder: { label: "Vibe Coder", gradient: "bg-primary" },
  investor: { label: "Investor", gradient: "bg-primary" },
  technical: { label: "Tech Lead", gradient: "bg-primary" },
};

export function ProfileCard({
  name,
  role,
  avatar,
  tagline,
  location,
  tags,
  matchScore,
}: ProfileCardProps) {
  const config = roleConfig[role];

  return (
    <Card className="hover-elevate overflow-visible border-border/50" data-testid={`card-profile-${name.toLowerCase().replace(/\s+/g, '-')}`}>
      <CardContent className="p-8">
        <div className="flex items-start gap-5">
          <Avatar className="h-20 w-20 border-2 border-border">
            <AvatarImage src={avatar} alt={name} />
            <AvatarFallback>{name.split(" ").map(n => n[0]).join("")}</AvatarFallback>
          </Avatar>

          <div className="flex-1 min-w-0">
            <h3 className="font-bold text-xl mb-2" data-testid="text-profile-name">{name}</h3>

            <div className="flex items-center gap-2 mb-3 flex-wrap">
              <Badge className={`${config.gradient} text-white shadow-sm`}>
                {config.label}
              </Badge>
              {matchScore && (
                <Badge className="bg-primary text-white">
                  {matchScore}% Match
                </Badge>
              )}
            </div>

            <p className="text-sm text-muted-foreground mb-4 line-clamp-2 leading-relaxed">
              {tagline}
            </p>

            <div className="flex items-center gap-2 text-xs text-muted-foreground mb-4">
              <MapPin className="h-3.5 w-3.5" />
              <span>{location}</span>
            </div>

            <div className="flex flex-wrap gap-2">
              {tags.slice(0, 3).map((tag) => (
                <Badge key={tag} variant="secondary" className="text-xs font-medium">
                  {tag}
                </Badge>
              ))}
              {tags.length > 3 && (
                <Badge variant="secondary" className="text-xs font-medium">
                  +{tags.length - 3}
                </Badge>
              )}
            </div>
          </div>
        </div>
      </CardContent>

      <CardFooter className="p-8 pt-0 flex gap-3">
        <Button variant="default" className="flex-1" data-testid="button-view-profile">
          View Profile
        </Button>
        <Button variant="outline" className="flex-1" data-testid="button-connect">
          Connect
        </Button>
      </CardFooter>
    </Card>
  );
}

