import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { ThemeProvider } from "@/components/ThemeProvider";
import { Header } from "@/components/Header";
import Home from "@/pages/Home";
import Discover from "@/pages/Discover";
import Matches from "@/pages/Matches";
import Discussions from "@/pages/Discussions";
import Projects from "@/pages/Projects";
import ProjectDashboard from "@/pages/ProjectDashboard";
import NotFound from "@/pages/not-found";

function Router() {
  return (
    <Switch>
      <Route path="/" component={Home} />
      <Route path="/discover" component={Discover} />
      <Route path="/matches" component={Matches} />
      <Route path="/discussions" component={Discussions} />
      <Route path="/projects" component={Projects} />
      <Route path="/projects/:id" component={ProjectDashboard} />
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <ThemeProvider>
        <TooltipProvider>
          <div className="min-h-screen">
            <Header />
            <Router />
          </div>
          <Toaster />
        </TooltipProvider>
      </ThemeProvider>
    </QueryClientProvider>
  );
}

export default App;

client/src/components/TeamSection.tsx.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Switch } from "@/components/ui/switch";
import { Label } from "@/components/ui/label";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertProjectMemberSchema } from "@shared/schema";
import type { ProjectMember } from "@shared/schema";
import { z } from "zod";
import { Plus, Users, DollarSign, PieChart } from "lucide-react";
import { format } from "date-fns";

const formSchema = insertProjectMemberSchema.extend({
  profileId: z.string().min(1, "Profile ID is required"),
  role: z.string().min(1, "Role is required"),
  compensationType: z.string().min(1, "Compensation type is required"),
}).omit({ projectId: true });

interface TeamSectionProps {
  projectId: string;
  members: ProjectMember[];
  isOwner: boolean;
  currentUserId: string;
  hasFullAccess: boolean;
}

export function TeamSection({ projectId, members, isOwner, currentUserId, hasFullAccess }: TeamSectionProps) {
  const [dialogOpen, setDialogOpen] = useState(false);
  const { toast } = useToast();

  const form = useForm<z.infer<typeof formSchema>>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      profileId: "",
      role: "technical",
      compensationType: "ownership",
      compensationAmount: "0",
      ownershipPercentage: "0",
    },
  });

  const compensationType = form.watch("compensationType");

  const addMember = useMutation({
    mutationFn: async (data: z.infer<typeof formSchema>) => {
      return await apiRequest("POST", `/api/projects/${projectId}/members`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/projects", projectId, "members"] });
      setDialogOpen(false);
      form.reset();
    },
  });

  const toggleAccess = useMutation({
    mutationFn: async ({ memberId, hasAccess }: { memberId: string; hasAccess: boolean }) => {
      return await apiRequest(
        "PATCH", 
        `/api/project-members/${memberId}`, 
        { hasAccess },
        { 'x-user-id': currentUserId }
      );
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/projects", projectId, "members"] });
      toast({
        title: "Access updated",
        description: "Team member dashboard access has been updated.",
      });
    },
    onError: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/projects", projectId, "members"] });
      toast({
        title: "Error",
        description: "Failed to update access. You must be the project owner.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: z.infer<typeof formSchema>) => {
    addMember.mutate(data);
  };

  // Filter members based on access level
  const visibleMembers = hasFullAccess 
    ? members 
    : members.filter(m => m.profileId === currentUserId);

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h3 className="text-2xl font-bold">{hasFullAccess ? 'Team Members' : 'My Team Info'}</h3>
          <p className="text-sm text-muted-foreground">
            {hasFullAccess 
              ? 'Manage collaborators and their compensation'
              : 'View your team membership information'
            }
          </p>
        </div>

        {hasFullAccess && (
          <Dialog open={dialogOpen} onOpenChange={setDialogOpen}>
            <DialogTrigger asChild>
              <Button data-testid="button-add-member">
                <Plus className="h-4 w-4 mr-2" />
                Add Member
              </Button>
            </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Add Team Member</DialogTitle>
            </DialogHeader>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
                <FormField
                  control={form.control}
                  name="profileId"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Profile ID</FormLabel>
                      <FormControl>
                        <Input placeholder="Enter profile ID" {...field} data-testid="input-member-profile-id" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="role"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Role</FormLabel>
                      <Select onValueChange={field.onChange} defaultValue={field.value}>
                        <FormControl>
                          <SelectTrigger data-testid="select-member-role">
                            <SelectValue placeholder="Select role" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="technical">Technical Lead</SelectItem>
                          <SelectItem value="investor">Investor</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="compensationType"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Compensation Type</FormLabel>
                      <Select onValueChange={field.onChange} defaultValue={field.value}>
                        <FormControl>
                          <SelectTrigger data-testid="select-compensation-type">
                            <SelectValue placeholder="Select compensation type" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="money">Money</SelectItem>
                          <SelectItem value="ownership">Ownership Stake</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {compensationType === "money" ? (
                  <FormField
                    control={form.control}
                    name="compensationAmount"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Payment Amount ($)</FormLabel>
                        <FormControl>
                          <Input 
                            type="number" 
                            placeholder="10000" 
                            {...field}
                            value={field.value || ""}
                            data-testid="input-compensation-amount"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                ) : (
                  <FormField
                    control={form.control}
                    name="ownershipPercentage"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Ownership Percentage (%)</FormLabel>
                        <FormControl>
                          <Input 
                            type="number" 
                            placeholder="15" 
                            step="0.1"
                            min="0"
                            max="100"
                            {...field}
                            value={field.value || ""}
                            data-testid="input-ownership-percentage"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                )}

                <div className="flex gap-3 justify-end">
                  <Button type="button" variant="outline" onClick={() => setDialogOpen(false)}>
                    Cancel
                  </Button>
                  <Button type="submit" disabled={addMember.isPending} data-testid="button-submit-member">
                    {addMember.isPending ? "Adding..." : "Add Member"}
                  </Button>
                </div>
              </form>
            </Form>
          </DialogContent>
        </Dialog>
        )}
      </div>

      {visibleMembers.length === 0 ? (
        <Card className="border-border/50">
          <CardContent className="p-16 text-center">
            <Users className="h-16 w-16 mx-auto mb-4 text-muted-foreground" />
            <h3 className="text-xl font-semibold mb-2">{hasFullAccess ? 'No team members yet' : 'Not a team member'}</h3>
            <p className="text-muted-foreground mb-6">
              {hasFullAccess 
                ? 'Add technical leads or investors to your project'
                : 'You are not currently a team member of this project'
              }
            </p>
            {hasFullAccess && (
              <Button onClick={() => setDialogOpen(true)} data-testid="button-add-first-member">
                <Plus className="h-4 w-4 mr-2" />
                Add Member
              </Button>
            )}
          </CardContent>
        </Card>
      ) : (
        <div className="grid gap-6">
          {visibleMembers.map((member) => (
            <Card key={member.id} className="border-border/50 hover-elevate" data-testid={`card-member-${member.id}`}>
              <CardContent className="p-6">
                <div className="flex items-start justify-between gap-4">
                  <div className="flex items-start gap-4 flex-1">
                    <Avatar className="h-16 w-16 border-2 border-border">
                      <AvatarFallback className="text-lg">
                        {member.role === 'technical' ? 'TL' : 'IN'}
                      </AvatarFallback>
                    </Avatar>
                    <div className="flex-1">
                      <div className="flex items-center gap-2 mb-2">
                        <h4 className="text-lg font-bold">Profile {member.profileId.slice(-6)}</h4>
                        <Badge 
                          className={
                            member.role === 'technical' 
                              ? 'bg-gradient-to-r from-violet-600 to-purple-600 text-white shadow-sm'
                              : 'bg-gradient-to-r from-emerald-600 to-teal-600 text-white shadow-sm'
                          }
                        >
                          {member.role === 'technical' ? 'Technical Lead' : 'Investor'}
                        </Badge>
                      </div>
                      <p className="text-sm text-muted-foreground mb-3">
                        Joined {format(new Date(member.joinedAt!), 'MMM d, yyyy')}
                      </p>

                      <div className="flex items-center gap-6">
                        {member.compensationType === 'money' && member.compensationAmount ? (
                          <div className="flex items-center gap-2">
                            <div className="p-2 rounded-lg bg-gradient-to-br from-green-500 to-emerald-500">
                              <DollarSign className="h-4 w-4 text-white" />
                            </div>
                            <div>
                              <p className="text-xs text-muted-foreground">Payment</p>
                              <p className="font-semibold">
                                ${parseFloat(member.compensationAmount).toLocaleString()}
                              </p>
                            </div>
                          </div>
                        ) : member.compensationType === 'ownership' && member.ownershipPercentage ? (
                          <div className="flex items-center gap-2">
                            <div className="p-2 rounded-lg bg-gradient-to-br from-purple-500 to-pink-500">
                              <PieChart className="h-4 w-4 text-white" />
                            </div>
                            <div>
                              <p className="text-xs text-muted-foreground">Ownership</p>
                              <p className="font-semibold">
                                {parseFloat(member.ownershipPercentage).toFixed(1)}%
                              </p>
                            </div>
                          </div>
                        ) : null}
                      </div>
                    </div>
                  </div>

                  {isOwner && (
                    <div className="flex items-center gap-3 pl-4 border-l border-border">
                      <div className="flex flex-col gap-1.5">
                        <Label htmlFor={`access-${member.id}`} className="text-xs text-muted-foreground">
                          Dashboard Access
                        </Label>
                        <div className="flex items-center gap-2">
                          <Switch
                            id={`access-${member.id}`}
                            checked={member.hasAccess}
                            onCheckedChange={(checked) => {
                              toggleAccess.mutate({ memberId: member.id, hasAccess: checked });
                            }}
                            disabled={toggleAccess.isPending}
                            data-testid={`switch-access-${member.id}`}
                          />
                          <span className="text-sm">
                            {member.hasAccess ? (
                              <Badge 
                                variant="default" 
                                className="bg-gradient-to-r from-emerald-600 to-teal-600 text-white shadow-sm"
                                data-testid={`badge-access-status-${member.id}`}
                              >
                                Granted
                              </Badge>
                            ) : (
                              <Badge 
                                variant="secondary"
                                data-testid={`badge-access-status-${member.id}`}
                              >
                                Not Granted
                              </Badge>
                            )}
                          </span>
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, timestamp, integer, decimal, boolean } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  email: varchar("email").unique(),
  firstName: varchar("first_name"),
  lastName: varchar("last_name"),
  profileImageUrl: varchar("profile_image_url"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const insertUserSchema = createInsertSchema(users).omit({
  createdAt: true,
  updatedAt: true,
});

export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;

// Profiles for different user types
export const profiles = pgTable("profiles", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").references(() => users.id).unique(), // One profile per user
  name: text("name").notNull(),
  role: varchar("role", { length: 20 }).notNull(), // 'coder', 'investor', 'technical'
  avatar: text("avatar"),
  tagline: text("tagline"),
  location: text("location"),
  tags: text("tags").array(),
  createdAt: timestamp("created_at").defaultNow(),
});

export const insertProfileSchema = createInsertSchema(profiles).omit({
  id: true,
  createdAt: true,
});

export type InsertProfile = z.infer<typeof insertProfileSchema>;
export type Profile = typeof profiles.$inferSelect;

// Projects - partnerships between vibe coders and technical persons
export const projects = pgTable("projects", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  description: text("description"),
  vibeCodeId: varchar("vibe_coder_id").notNull().references(() => profiles.id),
  status: varchar("status", { length: 20 }).notNull().default('active'), // 'active', 'completed', 'archived'
  vision: text("vision"),
  techStack: text("tech_stack").array(),
  targetUsers: text("target_users"),
  currentStage: varchar("current_stage", { length: 20 }), // 'idea', 'prototype', 'mvp', 'launched'
  lookingFor: text("looking_for").array(), // ['funding', 'technical', 'marketing', 'design']
  fundingGoal: decimal("funding_goal", { precision: 12, scale: 2 }),

  // Links & Platform
  projectLinks: text("project_links").array(), // GitHub, demo, website, social links
  platform: text("platform"), // Platform used to build project (e.g., Replit, VS Code, etc.)

  // Investor Targeting
  investorPreferences: text("investor_preferences"), // JSON string with detailed preferences
  targetFundingStage: varchar("target_funding_stage", { length: 30 }), // 'pre-seed', 'seed', 'series-a', etc.
  targetInvestorTypes: text("target_investor_types").array(), // ['angel', 'vc', 'strategic', 'accelerator']
  minInvestmentAmount: decimal("min_investment_amount", { precision: 12, scale: 2 }),
  maxInvestmentAmount: decimal("max_investment_amount", { precision: 12, scale: 2 }),

  // Technical Lead Requirements
  technicalRequirements: text("technical_requirements"), // JSON string with detailed requirements
  requiredSkills: text("required_skills").array(), // Required technical skills
  experienceLevel: varchar("experience_level", { length: 20 }), // 'junior', 'mid', 'senior', 'lead'
  timeCommitment: varchar("time_commitment", { length: 30 }), // 'full-time', 'part-time', 'contract', 'advisor'
  technicalRoles: text("technical_roles").array(), // ['backend', 'frontend', 'devops', 'mobile', 'ai/ml']

  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const insertProjectSchema = createInsertSchema(projects).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export type InsertProject = z.infer<typeof insertProjectSchema>;
export type Project = typeof projects.$inferSelect;

// Project members - technical persons and investors in a project
export const projectMembers = pgTable("project_members", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  projectId: varchar("project_id").notNull().references(() => projects.id),
  profileId: varchar("profile_id").notNull().references(() => profiles.id),
  role: varchar("role", { length: 20 }).notNull(), // 'technical', 'investor'
  compensationType: varchar("compensation_type", { length: 20 }).notNull(), // 'money', 'ownership'
  compensationAmount: decimal("compensation_amount", { precision: 10, scale: 2 }), // for money
  ownershipPercentage: decimal("ownership_percentage", { precision: 5, scale: 2 }), // for ownership
  hasAccess: boolean("has_access").notNull().default(false), // dashboard access granted by vibe coder
  joinedAt: timestamp("joined_at").defaultNow(),
});

export const insertProjectMemberSchema = createInsertSchema(projectMembers).omit({
  id: true,
  joinedAt: true,
});

export type InsertProjectMember = z.infer<typeof insertProjectMemberSchema>;
export type ProjectMember = typeof projectMembers.$inferSelect;

// Code repositories for evaluation
export const codeRepositories = pgTable("code_repositories", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  projectId: varchar("project_id").notNull().references(() => projects.id),
  uploadedBy: varchar("uploaded_by").notNull().references(() => profiles.id),
  name: text("name").notNull(),
  description: text("description"),
  fileUrl: text("file_url").notNull(), // URL or path to uploaded code
  language: text("language"),
  linesOfCode: integer("lines_of_code"),
  estimatedHours: integer("estimated_hours"),
  estimatedCost: decimal("estimated_cost", { precision: 10, scale: 2 }),
  status: varchar("status", { length: 20 }).notNull().default('pending'), // 'pending', 'reviewed', 'approved'
  uploadedAt: timestamp("uploaded_at").defaultNow(),
});

export const insertCodeRepositorySchema = createInsertSchema(codeRepositories).omit({
  id: true,
  uploadedAt: true,
});

export type InsertCodeRepository = z.infer<typeof insertCodeRepositorySchema>;
export type CodeRepository = typeof codeRepositories.$inferSelect;

// Project documents - business plans, pitch decks, etc.
export const projectDocuments = pgTable("project_documents", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  projectId: varchar("project_id").notNull().references(() => projects.id),
  uploadedBy: varchar("uploaded_by").notNull().references(() => profiles.id),
  name: text("name").notNull(),
  description: text("description"),
  fileUrl: text("file_url").notNull(), // URL or path to uploaded document
  fileType: varchar("file_type", { length: 50 }), // 'pitch-deck', 'business-plan', 'roadmap', 'whitepaper', 'other'
  mimeType: varchar("mime_type", { length: 100 }), // e.g., 'application/pdf', 'image/png'
  fileSize: integer("file_size"), // in bytes
  uploadedAt: timestamp("uploaded_at").defaultNow(),
});

export const insertProjectDocumentSchema = createInsertSchema(projectDocuments).omit({
  id: true,
  uploadedAt: true,
});

export type InsertProjectDocument = z.infer<typeof insertProjectDocumentSchema>;
export type ProjectDocument = typeof projectDocuments.$inferSelect;

// Tasks within projects
export const tasks = pgTable("tasks", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  projectId: varchar("project_id").notNull().references(() => projects.id),
  title: text("title").notNull(),
  description: text("description"),
  assignedTo: varchar("assigned_to").references(() => profiles.id),
  createdBy: varchar("created_by").notNull().references(() => profiles.id),
  status: varchar("status", { length: 20 }).notNull().default('todo'), // 'todo', 'in-progress', 'completed'
  priority: varchar("priority", { length: 20 }).default('medium'), // 'low', 'medium', 'high'
  estimatedHours: integer("estimated_hours"),
  dueDate: timestamp("due_date"),
  createdAt: timestamp("created_at").defaultNow(),
  completedAt: timestamp("completed_at"),
});

export const insertTaskSchema = createInsertSchema(tasks).omit({
  id: true,
  createdAt: true,
  completedAt: true,
});

export type InsertTask = z.infer<typeof insertTaskSchema>;
export type Task = typeof tasks.$inferSelect;

// Contributions tracking for IP value estimation
export const contributions = pgTable("contributions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  projectId: varchar("project_id").notNull().references(() => projects.id),
  contributorId: varchar("contributor_id").notNull().references(() => profiles.id),
  taskId: varchar("task_id").references(() => tasks.id),
  type: varchar("type", { length: 20 }).notNull(), // 'code', 'design', 'idea', 'funding', 'management'
  description: text("description"),
  hoursSpent: integer("hours_spent"),
  valueScore: integer("value_score").default(0), // 0-100 score for IP value
  createdAt: timestamp("created_at").defaultNow(),
});

export const insertContributionSchema = createInsertSchema(contributions).omit({
  id: true,
  createdAt: true,
});

export type InsertContribution = z.infer<typeof insertContributionSchema>;
export type Contribution = typeof contributions.$inferSelect;

// Matches/Connections between users
export const matches = pgTable("matches", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  initiatorId: varchar("initiator_id").notNull().references(() => profiles.id),
  receiverId: varchar("receiver_id").notNull().references(() => profiles.id),
  status: varchar("status", { length: 20 }).notNull().default('pending'), // 'pending', 'accepted', 'rejected'
  matchType: varchar("match_type", { length: 20 }).notNull(), // 'collaboration', 'investment', 'technical', 'mentorship'
  matchReason: text("match_reason"), // AI-generated reason for the match
  message: text("message"),
  createdAt: timestamp("created_at").defaultNow(),
  respondedAt: timestamp("responded_at"),
});

export const insertMatchSchema = createInsertSchema(matches).omit({
  id: true,
  createdAt: true,
  respondedAt: true,
});

export type InsertMatch = z.infer<typeof insertMatchSchema>;
export type Match = typeof matches.$inferSelect;

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  // Keep track of registered strategies
  const registeredStrategies = new Set<string>();

  // Helper function to ensure strategy exists for a domain
  const ensureStrategy = (domain: string) => {
    const strategyName = `replitauth:${domain}`;
    if (!registeredStrategies.has(strategyName)) {
      const strategy = new Strategy(
        {
          name: strategyName,
          config,
          scope: "openid email profile offline_access",
          callbackURL: `https://${domain}/api/callback`,
        },
        verify,
      );
      passport.use(strategy);
      registeredStrategies.add(strategyName);
    }
  };

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    ensureStrategy(req.hostname);
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    ensureStrategy(req.hostname);
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  // In development/test mode, allow x-user-id header for testing
  const allowDevAuth = process.env.NODE_ENV === 'development' || process.env.NODE_ENV === 'test';
  if (allowDevAuth) {
    const testUserId = req.headers['x-user-id'];
    if (testUserId) {
      return next();
    }
  }

  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

client/src/hooks/useAuth.ts.content:
import { useQuery } from "@tanstack/react-query";
import { useEffect, useState } from "react";
import type { User } from "@shared/schema";

export function useAuth() {
  // In development, allow test user ID from URL parameter (no default fallback)
  const isDev = import.meta.env.DEV;
  const urlParams = new URLSearchParams(window.location.search);
  const testUserId = urlParams.get('testUserId');
  const [authReady, setAuthReady] = useState(!isDev || !testUserId);

  // Store test user ID in localStorage for requests (if provided)
  useEffect(() => {
    if (isDev && testUserId) {
      localStorage.setItem('test-user-id', testUserId);
      setAuthReady(true);
    }
  }, [isDev, testUserId]);

  const { data: user, isLoading } = useQuery<User | null>({
    queryKey: ["/api/auth/user"],
    retry: false,
    enabled: authReady, // Only query after localStorage is set
  });

  return {
    user,
    isLoading: isLoading || !authReady,
    isAuthenticated: !!user,
  };
}

server/aiMatchingService.ts.content:
import OpenAI from "openai";
import type { Profile, Project } from "@shared/schema";

const openai = new OpenAI({
  apiKey: process.env.AI_INTEGRATIONS_OPENAI_API_KEY,
  baseURL: process.env.AI_INTEGRATIONS_OPENAI_BASE_URL,
});

interface MatchCandidate {
  profile: Profile;
  projects?: Project[];
}

interface MatchResult {
  profileId: string;
  matchScore: number;
  matchReason: string;
  matchType: "collaboration" | "investment" | "technical" | "mentorship";
}

export async function generateMatches(
  currentUser: Profile,
  currentUserProjects: Project[],
  candidates: MatchCandidate[],
  limit: number = 10
): Promise<MatchResult[]> {
  if (candidates.length === 0) {
    return [];
  }

  const prompt = buildMatchingPrompt(currentUser, currentUserProjects, candidates);

  try {
    const completion = await openai.chat.completions.create({
      model: "gpt-4o-mini",
      messages: [
        {
          role: "system",
          content: `You are an expert matchmaking AI for a professional networking platform called VibeMatch. 
Your job is to analyze user profiles and intelligently match:
- Vibe Coders (entrepreneurs/builders) with Investors and Tech Leads
- Investors with Vibe Coders who have promising projects
- Tech Leads with Vibe Coders who need technical leadership

Consider: skills alignment, project fit, location proximity, complementary expertise, and strategic value.
Return match scores (0-100) with specific, actionable reasons.`,
        },
        {
          role: "user",
          content: prompt,
        },
      ],
      response_format: { type: "json_object" },
      temperature: 0.7,
    });

    const content = completion.choices[0]?.message?.content;
    if (!content) {
      return [];
    }

    const aiResponse = JSON.parse(content);
    const matches: MatchResult[] = aiResponse.matches || [];

    return matches
      .filter((m: MatchResult) => m.matchScore >= 50)
      .sort((a: MatchResult, b: MatchResult) => b.matchScore - a.matchScore)
      .slice(0, limit);
  } catch (error) {
    console.error("AI matching error:", error);
    return [];
  }
}

function buildMatchingPrompt(
  currentUser: Profile,
  currentUserProjects: Project[],
  candidates: MatchCandidate[]
): string {
  const userDescription = `
Current User Profile:
- Name: ${currentUser.name}
- Role: ${currentUser.role}
- Tagline: ${currentUser.tagline || "N/A"}
- Location: ${currentUser.location || "N/A"}
- Skills/Tags: ${currentUser.tags?.join(", ") || "N/A"}
${
  currentUserProjects.length > 0
    ? `
- Projects:
${currentUserProjects
  .map(
    (p) => `  * ${p.name}: ${p.description || "N/A"}
    - Stage: ${p.currentStage || "N/A"}
    - Looking for: ${p.lookingFor?.join(", ") || "N/A"}
    - Tech Stack: ${p.techStack?.join(", ") || "N/A"}
    - Funding Goal: ${p.fundingGoal ? `$${p.fundingGoal}` : "N/A"}`
  )
  .join("\n")}`
    : ""
}
`;

  const candidatesDescription = candidates
    .map((c, idx) => {
      const projects = c.projects || [];
      return `
Candidate ${idx + 1}:
- ID: ${c.profile.id}
- Name: ${c.profile.name}
- Role: ${c.profile.role}
- Tagline: ${c.profile.tagline || "N/A"}
- Location: ${c.profile.location || "N/A"}
- Skills/Tags: ${c.profile.tags?.join(", ") || "N/A"}
${
  projects.length > 0
    ? `- Projects:
${projects
  .map(
    (p) => `  * ${p.name}: ${p.description || "N/A"}
    - Stage: ${p.currentStage || "N/A"}
    - Looking for: ${p.lookingFor?.join(", ") || "N/A"}
    - Tech Stack: ${p.techStack?.join(", ") || "N/A"}`
  )
  .join("\n")}`
    : ""
}`;
    })
    .join("\n---\n");

  return `${userDescription}

---
Candidate Profiles to Match:
${candidatesDescription}

---
Instructions:
Analyze each candidate and determine their match quality with the current user.
For each candidate, provide:
1. matchScore (0-100): How well they match
2. matchReason: A specific, actionable 1-2 sentence explanation
3. matchType: "collaboration", "investment", "technical", or "mentorship"

Only include candidates with a match score of 50 or higher.

Return your response as JSON in this format:
{
  "matches": [
    {
      "profileId": "candidate-id",
      "matchScore": 85,
      "matchReason": "Strong technical alignment with React and Node.js skills. Their experience in scalable systems matches your MVP stage project needs.",
      "matchType": "technical"
    }
  ]
}`;
}

export async function explainMatch(
  user1: Profile,
  user2: Profile,
  user1Projects?: Project[],
  user2Projects?: Project[]
): Promise<{ reason: string; matchType: string }> {
  const prompt = `
User 1:
- Name: ${user1.name}
- Role: ${user1.role}
- Skills: ${user1.tags?.join(", ") || "N/A"}
- Tagline: ${user1.tagline || "N/A"}
${user1Projects?.length ? `- Has ${user1Projects.length} project(s)` : ""}

User 2:
- Name: ${user2.name}
- Role: ${user2.role}
- Skills: ${user2.tags?.join(", ") || "N/A"}
- Tagline: ${user2.tagline || "N/A"}
${user2Projects?.length ? `- Has ${user2Projects.length} project(s)` : ""}

Explain in 1-2 sentences why these two users would be a good match. Also determine the match type: "collaboration", "investment", "technical", or "mentorship".

Return JSON:
{
  "reason": "explanation here",
  "matchType": "technical"
}`;

  try {
    const completion = await openai.chat.completions.create({
      model: "gpt-4o-mini",
      messages: [
        {
          role: "system",
          content:
            "You are a matchmaking expert. Provide concise, specific match explanations.",
        },
        { role: "user", content: prompt },
      ],
      response_format: { type: "json_object" },
      temperature: 0.7,
    });

    const content = completion.choices[0]?.message?.content;
    if (content) {
      return JSON.parse(content);
    }
  } catch (error) {
    console.error("AI explain match error:", error);
  }

  return {
    reason: "Potential collaboration opportunity based on complementary skills.",
    matchType: "collaboration",
  };
}

